package eu.pp.cashwizard.view.adapters;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.TextView;import java.math.BigDecimal;import java.util.List;import eu.pp.cashwizard.R;import eu.pp.cashwizard.dict.Currency;import eu.pp.cashwizard.util.JUtil;import eu.pp.cashwizard.view.model.PaymentsListRow;import lombok.NonNull;public class IncomesListAdapter extends ArrayAdapter<PaymentsListRow> {    public IncomesListAdapter(@NonNull Context context, @NonNull List<PaymentsListRow> incomes ) {        super(context, 0, incomes );    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        PaymentsListRow lr = getItem(position);        if (convertView == null) {            convertView = LayoutInflater.from(getContext()).inflate(R.layout.row_incomes_list, parent, false);        }        TextView num = (TextView) convertView.findViewById(R.id.lrIncomeNr);        TextView date = (TextView) convertView.findViewById(R.id.lrIncomeDate);        TextView person = (TextView) convertView.findViewById(R.id.lrIncomePerson);        TextView amount = (TextView) convertView.findViewById(R.id.lrIncomeAmount);        TextView currency = (TextView) convertView.findViewById(R.id.lrIncomeCurrency);        num.setText( "" + ( position + 1 ) );        date.setText( JUtil.getDate( lr.payment.getDate() ) );        person.setText( lr.payment.getPerson().getNickName() );        amount.setText( "" + lr.payment.getCash().getAmount() );        currency.setText( "" + lr.payment.getCash().getCurrency() );        return convertView;    }}